{u'Fn::Join': [u'', [u'user  nginx;
', u'worker_processes  1;

', u'error_log  /var/log/nginx/error.log;
', u'pid        /var/run/nginx.pid;

', u'events {
', u'    worker_connections  1024;
', u'}

', u'http {
', u'    include       /etc/nginx/mime.types;
', u'    default_type  application/octet-stream;
', u'    log_format  main  '$remote_addr - $remote_user [$time_local] "$request" '
', u'                      '$status $body_bytes_sent "$http_referer" '
', u'                      '"$http_user_agent" "$http_x_forwarded_for"';

', u'    access_log  /var/log/nginx/access.log  main;
', u'    sendfile        on;
', u'    keepalive_timeout  65;
', u'    include /etc/nginx/conf.d/*.conf;
', u'    index   index.html index.htm;
', u'    server {
', u'        listen       80;
', u'        server_name  _;
', u'        location / {
', u'          proxy_pass              http://127.0.0.1:8080;
', u'          proxy_set_header        Host $host;
', u'          proxy_set_header        X-Real-IP $remote_addr;
', u'          proxy_set_header        X-Forwarded-For $proxy_add_x_forwarded_for;
', u'          proxy_connect_timeout   150;
', u'          proxy_send_timeout      100;
', u'          proxy_read_timeout      100;
', u'          proxy_buffers           4 32k;
', u'          client_max_body_size    8m;
', u'          client_body_buffer_size 128k;
', u'        }
', u'   }
', u'}
']]}
